public class pattern10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 5; i++) {
			for (int s = i; s <= 5; s++) {
				System.out.print(" ");

			}
			for (int j = 1; j <= 2; j++) {

				System.out.print("#@");
			}

			System.out.println();

		}

	}

}
